mapper.delete:
delete
from ${tableName}
where course_name like concat('%', #{courseName}, '%');

mapper.update:
update ${tableName}
course_name = #{course.courseName}, course_location = #{course.courseLocation}, course_lecturer = #{course.courseLecturer}, course_date = #{course.courseDate}, course_week = #{course.courseWeek},
where course_id = #{course.courseId};

mapper.select:
select course_id, course_name, course_location, course_lecturer, course_date, course_week
from ${tableName}
where course_id = #{id};

select course_id, course_name, course_location, course_lecturer, course_date, course_week
from ${tableName}
where course_name = #{courseName};

select course_id, course_name, course_location, course_lecturer, course_date, course_week
from ${tableName}
where course_date like concat('%', #{date}, '%');
